import{_ as t}from"./chunks/image-26.DnVZ_Cmd.js";import{_ as a}from"./chunks/nerd.ThEq67B6.js";import{_ as s,c as r,a0 as o,o as n}from"./chunks/framework.CMtr6uOw.js";const i="/assets/image-28.CJUUWp5D.png",w=JSON.parse('{"title":"Matject settings explained","description":"","frontmatter":{},"headers":[],"relativePath":"docs/matject-settings-explained.md","filePath":"docs/matject-settings-explained.md"}'),p={name:"docs/matject-settings-explained.md"};function c(l,e,d,h,m,g){return n(),r("div",null,e[0]||(e[0]=[o('<h1 id="matject-settings-explained" tabindex="-1">Matject settings explained <a class="header-anchor" href="#matject-settings-explained" aria-label="Permalink to &quot;Matject settings explained&quot;">​</a></h1><p>Matject has many options in settings to greatly enhance user experience.</p><p>Here, I explained every settings (as of v3.5.0)</p><p><a href="#general">General</a><br><a href="#custom-paths">Custom paths</a><br><a href="#matjectnext-settings">matjectNEXT settings</a><br><a href="#updates-debug">Updates &amp; Debug</a></p><br><details class="details custom-block"><summary>How to open settings and navigate...</summary><p>To open Matject settings press <strong>[S]</strong> in the main screen.</p><p><img src="'+t+'" alt="alt text" loading="lazy"></p><p><br><br></p><p><img src="'+i+'" alt="alt text" loading="lazy"></p><p>Press <strong>[A]</strong> or <strong>[D]</strong> to switch tab (go left or right)<br> All options are labelled with the key to press.</p></details><p><br><br></p><h2 id="general" tabindex="-1">General <a class="header-anchor" href="#general" aria-label="Permalink to &quot;General&quot;">​</a></h2><p>Pressing <strong>[0]</strong> will open <code>.settings</code> folder where Matject stores its configurations and other data.</p><hr><p><strong>Default Method:</strong><br> Sets the method to automatically open when Matject is started.<br> Press [1] to switch between options.</p><hr><p><strong>Use material-updater to update materials:</strong><br> Enables material-updater. <a href="/docs/what-is-material-updater.html">[?]</a><br> Usually fixes invisible block and other version specific issues.</p><hr><p><strong>Disable confirmations:</strong><br> Disables confirmations in file selection, injection and some more...</p><hr><p><strong>Don&#39;t retain old backups:</strong><br> Deletes old backup automatically when making backup of new Minecraft version files.</p><hr><p><strong>Disable success message:</strong><br> Disables the success/thank you message of auto and manual method.</p><hr><p><strong>Auto import MCPACK after injection:</strong><br> For auto method only.<br> Opens provided MCPACK/ZIP file with associated app.</p><hr><p><strong>Don&#39;t open folder automatically:</strong><br> Disables auto folder opening for auto and manual method.<br> For those who add files before opening Matject.</p><hr><p><strong>Use for Minecraft Preview:</strong><br> Makes Matject use Minecraft Preview instead.<br> Some settings are kept separate for it. So, you can use it for any Minecraft at any time.</p><hr><p><strong>Disable tips:</strong><br> Disables helpful tips everywhere.<br> For those who are experienced enough <img style="display:inline;vertical-align:middle;" width="24px" alt=":nerd:" src="'+a+'" title="stolen from YSS discord server"></p><hr><p><strong>Show Matject announcements:</strong><br> Shows 1 line announcements in the main screen.<br> For those who want to stay up to date about Matject.</p><hr><p><br><br></p><h2 id="custom-paths" tabindex="-1">Custom paths <a class="header-anchor" href="#custom-paths" aria-label="Permalink to &quot;Custom paths&quot;">​</a></h2><p><strong>Use custom Minecraft app path:</strong><br> Sets path of Minecraft app automatically using PowerShell Get-AppxPackage command or by taking manual input from user.<br> Recommended for 3rd party Minecraft launcher users.<br> Enabling this prevents calling PowerShell at startup. Thus greatly improving startup speed on some PCs.</p><hr><p><strong>Use custom Minecraft data path:</strong><br> Sets path of Minecraft data folder.<br> Recommended for 3rd party Minecraft launcher users.<br> For those who wants to work with different data folder.</p><p>By default it is: <code>&quot;%LOCALAPPDATA%\\Packages\\Microsoft.MinecraftUWP_8wekyb3d8bbwe\\LocalState\\games\\com.mojang&quot;</code></p><hr><p><strong>Use custom IObit Unlocker path:</strong><br> Sets path of IObit Unlocker folder location.<br> For those who wants to use IObit Unlocker from a different location.</p><hr><p><br><br></p><h2 id="matjectnext-settings" tabindex="-1">matjectNEXT settings <a class="header-anchor" href="#matjectnext-settings" aria-label="Permalink to &quot;matjectNEXT settings&quot;">​</a></h2><p>Some settings dedicated for matjectNEXT only.</p><p><strong>Just sync and exit:</strong><br> Exits from matjectNEXT after sync is complete.</p><hr><p><strong>Disable manifest checker:</strong><br> Disables manifest checking.<br> Not recommended to enable.</p><p>This prevents you from finding out which resource pack has invalid <code>manifest.json</code>.</p><hr><p><br><br></p><h2 id="updates-debug" tabindex="-1">Updates &amp; Debug <a class="header-anchor" href="#updates-debug" aria-label="Permalink to &quot;Updates &amp; Debug&quot;">​</a></h2><p><strong>Check for updates at Matject startup:</strong><br> Checks for Matject updates when Matject is opened.</p><hr><p><strong>Disable interruption check:</strong><br> Disables power loss/interruption check.<br> Not recommended to enable.</p><p>Enabling this will skip the message that asks you to perform a full restore at Matject startup.</p><hr><p><strong>Disable material compatibility check:</strong><br> Disables checking <code>.material.bin</code> files.<br> Not recommended to enable.</p><p>Enabling this will allow injecting shaders that are not made for Windows.</p><hr><p><strong>Run IObit Unlocker as admin:</strong><br> Reduces admin permission requests to only 1.<br> Experimental. Only works with auto and manual method for now.</p><p>For those who find accepting IObit Unlocker admin permission requests annoying.<br> Also automatically closes IObit Unlocker messages.</p><hr><p><strong>Check for updates manually:</strong><br> Checks for Matject updates.</p><hr><p><strong>DEBUG MODE:</strong><br> Enables &quot;Restore &amp; Others -&gt; Drop to shell&quot;, matjectNEXT cache logging, detailed information about injections.</p><p>Intended for testing/debugging purpose.</p><hr><p><strong>Run Matject as admin always</strong><br> Runs Matject as administrator.<br> Effectively skipping every admin permission requests.</p><p>Selecting PowerShell will launch Matject using Start-Process command combined with -Verb runAs</p><p>Selecting VBscript will create a helper script in <code>.settings\\runAsAdmin.vbs</code> with a fixed path to <code>matject.bat</code> or <code>Matject.lnk</code> if user has made shortcut.</p>',68)]))}const k=s(p,[["render",c]]);export{w as __pageData,k as default};
