import{_ as e}from"./chunks/image-26.CKdMmLvb.js";import{_ as o,c as s,a0 as a,o as i}from"./chunks/framework.Csfegiu-.js";const l="/matject/assets/image-11.B5HYgOm-.png",r="/matject/assets/image-8.DVSybEMe.png",n="/matject/assets/image-14.D8Ih43cE.png",c="/matject/assets/image-15.NYyzo3u1.png",p="/matject/assets/image-18.D_qBTa93.png",d="/matject/assets/image-40.CSEu0tjj.png",m="/matject/assets/image-41.DZe-pl9o.png",u="/matject/assets/image-19.DY0bcCb9.png",h="/matject/assets/image-20.DnyJ5ePa.png",g="/matject/assets/image-21.U6P_gBqI.png",C=JSON.parse('{"title":"How to use auto method?","description":"","frontmatter":{},"headers":[],"relativePath":"docs/method-auto.md","filePath":"docs/method-auto.md"}'),b={name:"docs/method-auto.md"};function f(y,t,w,k,_,I){return i(),s("div",null,t[0]||(t[0]=[a('<h1 id="how-to-use-auto-method" tabindex="-1">How to use auto method? <a class="header-anchor" href="#how-to-use-auto-method" aria-label="Permalink to &quot;How to use auto method?&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">Conditions</p><p>The shader you want to use must meet some conditions.</p><ul><li>It must be specifically for Windows. (<strong>merged</strong> should also work if you know what I mean)</li><li>Should support your Minecraft version. Or you will see invisible blocks or game might crash. (can be fixed using <a href="/matject/docs/what-is-material-updater">material-updater</a>)</li><li>If it requires a resource pack, you should import and activate it.</li></ul></div><br><p>With that being said, let&#39;s get started...</p><ol><li><p>Get the MCPACK/ZIP file that you want to use. (no need to extract)</p></li><li><p>Put it in the <code>MCPACKS</code> folder.</p></li></ol><p><img src="'+l+'" alt="alt text" loading="lazy"><img src="'+r+'" alt="alt text" loading="lazy"></p><p><br><br></p><ol start="3"><li><p>Open <code>matject</code>.</p></li><li><p>In the main screen, press <strong>[1]</strong> for &quot;Auto method&quot;.</p></li></ol><p><img src="'+e+'" alt="alt text" loading="lazy"></p><p><br><br></p><ol start="5"><li>By default, it will open up the <code>MCPACKS</code> folder for you to put the file in there.<br> If you did &quot;step 2&quot; then simply close the window and press any key to continue.</li></ol><div class="tip custom-block"><p class="custom-block-title">Tip</p><p>You can disable auto folder opening from settings.</p></div><details class="details custom-block"><summary>Note about multiple MCPACK/ZIPs in the <code>MCPACKS</code> folder...</summary><p>If it finds multiple MCPACK/ZIPs in the <code>MCPACKS</code> folder it will show you a list where you can select any one by entering its number.</p><p><img src="'+n+'" alt="alt text" loading="lazy"></p><p>For example: If you write <code>13</code> and press <strong>[ENTER]</strong> it will pick the 13th item from the list.<br> In this case, it will pick &quot;newb-x-legacy-15.57-windows.mcpack&quot;</p></details><p><br><br></p><ol start="6"><li>Confirm your choice by pressing <strong>[Y]</strong> for yes and <strong>[N]</strong> for no.</li></ol><p><img src="'+c+'" alt="alt text" loading="lazy"></p><details class="details custom-block"><summary>Note about subpacks...</summary><p>If chosen file has subpacks it will show you a list where you can select any one by entering its number.</p><p><img src="'+p+'" alt="alt text" loading="lazy"></p><p>For example: If you write <code>6</code> and press <strong>[ENTER]</strong> it will pick the 6th item from the list.<br> In this case, it will pick &quot;rounded_clouds&quot;</p><div class="info custom-block"><p class="custom-block-title">Keep in mind</p><p>You also have to select the same subpack from resource packs settings later to make sure the shader works as expected.<br> The names shown in the list and in resource packs settings may not be the same always. In that case, you can check manifest.json inside the provided file to get an idea of what each in the list do.</p><p><img src="'+d+'" alt="alt text" loading="lazy"><br><img src="'+m+'" alt="alt text" loading="lazy"></p><p> </p><p>You can also write <code>/skip</code> and press <strong>[ENTER]</strong> and it will skip the subpack part<br> (only if you know what you&#39;re doing + it may cause shader to not work as expected.)</p></div></details><p><br><br></p><ol start="7"><li>If everything went good, it should show a list files that it is going to replace like this...</li></ol><p><img src="'+u+'" alt="alt text" loading="lazy"></p><p>Here, you can press,</p><p><strong>[Y]</strong> to confirm replacement/injection</p><p><strong>[R]</strong> to refresh the file list</p><div class="info custom-block"><p class="custom-block-title">Info</p><p>Use this if you want to include other <code>.material.bin</code> files in the <code>MATERIALS</code> as well.<br> After adding files, pressing <strong>[R]</strong> should show new files in the list.</p></div><p><strong>[N]</strong> to cancel.<br> It will return to the main screen.</p><p><br><br></p><ol start="8"><li>Accept admin permission requests.</li></ol><p><img src="'+h+'" alt="alt text" loading="lazy"></p><p>Based on the situation, it will ask you 2 or 4 times. You have to accept all.</p><p><br><br></p><h2 id="congratulations" tabindex="-1">Congratulations! <a class="header-anchor" href="#congratulations" aria-label="Permalink to &quot;Congratulations!&quot;">​</a></h2><p><strong>You have applied a shader succesfully.</strong></p><p>It&#39;s better to import the MCPACK and activate it if asked to do so.</p><p><img src="'+g+'" alt="alt text" loading="lazy"></p><p><br><br></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>If you&#39;re changing shaders, you don&#39;t have to restore manually.<br> It will automatically restore files as needed.</p></div>',36)]))}const P=o(b,[["render",f]]);export{C as __pageData,P as default};
